import React from "react";
import styled from "styled-components";

export default function Detail() {
  return (
    <Container>
      <h6>hello</h6>
      <h1>hello</h1>
      <h1>hello</h1>
      <Background>
        <img src="/images/login-background.jpg" alt="photo" />
      </Background>
    </Container>
  );
}

const Container = styled.main`
  position: relative;

  text-align: left;
  min-height: calc(100vh - 250px);
  overflow-x: hidden;
  display: block;
  top: 72px;
  padding: 0px calc(3.5vw + 5px);

  /* &::after {
    background: url("/images/home-background.png") center center / cover
      no-repeat fixed;
    content: "";
    position: absolute;
    inset: 0px;
    z-index: -1;
  } */
`;

const Background = styled.div`
  position: fixed;
  left: 0;
  top: 0;
opacity:0.8
  z-index: -1;
`;
